import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { IpfsService } from "../ipfs.service";
import { NgForm } from '@angular/forms'
//test for cat method
const { toString: uint8ArrayToString } = require('uint8arrays/to-string');

@Component({
  selector: 'app-upload',
  templateUrl: './upload.component.html',
  styleUrls: ['./upload.component.css']
})
export class UploadComponent implements OnInit {
  selectedFiles?: FileList;         //files selected by user with file broswer
  msg?: string;                    // holds message associated with upload
  FileInfos: any[] = [];          // list of all files uploaded
  reUploads: string[] = [];      // files in selected files that are already uploaded
  ipfsid: string | null = null;
  //metadata form
  showform: boolean = false;
  showcontracts: boolean = false;
  //use these if user selects set all forms same option
  metaname?: string;
  metades?: string;
  drone?: string;
  pilot?: string;
  date?: any;
  alt?: number;
  auto?: boolean;
  lat?: number;
  lon?: number;

  sameVars: boolean = false;

  constructor(private IPFSService: IpfsService) { }

  ngOnInit(): void {
    this.start();
  }

  async start() {
    const id = await this.IPFSService.getId();
    this.ipfsid = id.id;
    if(this.ipfsid == null){
      console.log("IPFS start up FAILED");
    }
    const stat = await this.IPFSService.getStatus();
    console.log(stat);

  /*  To check if node is connected to peers (sharing files)
    const peerInfos = await this.IPFSService.getPeers();
    const peers = await this.IPFSService.getPeers();
    console.log(peers);

    peerInfos.forEach(info => {
      console.log(info.id)
      info.addrs.forEach(addr => console.log(addr.toString()))
    })*/
  }

  //on click of choose files button
  onChange(event: any) {
    this.selectedFiles = event.target.files;
  }
  //on click of upload button
  onClick() {
    this.msg = "";
    this.reUploads = [];
    if(this.selectedFiles && this.selectedFiles[0]) { //if user has selected a file
      const numOfFiles = this.selectedFiles.length;  //gets length of selected files list

      for (let i = 0; i < numOfFiles; i++) { //for files selected
        let name = this.selectedFiles[i].name;
        let file = this.selectedFiles[i];
        let match = false;
        for (var t in this.FileInfos) {  //check if file has already been uploaded
            if (this.FileInfos[t].name == name) {
              match = true;
            }
          }

      if(match){
        this.reUploads.push(name);
        continue;  //skip to next iteration
      }

      const reader = new FileReader();
      reader.readAsDataURL(this.selectedFiles[i]); //read file content
      reader.onload = (event) => {                //when image is loaded
        var image = reader.result as string;
        if(image){
          let imageInfo = {
            id: i,
            name: name,
            url: image,
            blob: file,
            imghash: null,
            mname: null,
            desc: null,
            drone: null,
            pilot: null,
            date: null,
            alt: null,
            auto: null,
            lat: null,
            lon: null,
            metaHash: null
          };
          this.FileInfos.push(imageInfo);
          this.msg = "Success";

          //sort alphabetically by name
          const sorted = this.FileInfos.sort((t1, t2) => {
          const name1 = t1.name.toLowerCase();
          const name2 = t2.name.toLowerCase();
          if (name1 > name2) { return 1; }
          if (name1 < name2) { return -1; }
          return 0;
        })
      } else { //FileReader returned no url
          console.log("FAIL: File could not be read");
          this.msg = "Fail";
          }
        }; //end onload event
      } //end for
    } //end if
  } // end onClick()

  //file remove button
  onRemove(imageObj: any) {
    this.msg = "";
    var index = this.FileInfos.indexOf(imageObj);
    this.FileInfos.splice(index,1);
  }
  //ipfs upload function
  async ipfsUpload() {
    this.showform = true;
    if(this.FileInfos && this.FileInfos[0]) { //if user has selected a file
      const len = this.FileInfos.length;
      for(let i = 0; i < len; i++){
        this.FileInfos[i].imghash = await this.IPFSService.addFile(this.FileInfos[i].blob);
        console.log("FILE ADDED. Name: " + this.FileInfos[i].name + "Hash: " + this.FileInfos[i].imghash);
      }
    }
  }

  //submit metadata form and convert to JSON form
  async submitMeta(files: any[], linked: boolean) {
    if(linked){
      for(let i=0; i<files.length; i++){
        let data = {
          name: this.metaname,
          description: this.metades,
          image: "ipfs://" + files[i].imghash,
          ...(this.drone != null && {dronetype: this.drone}),
          ...(this.pilot&& {pilotname: this.pilot}),
          ...(this.date != null && {date: this.date}),
          ...(this.alt != null && {altitude: this.alt}),
          ...(this.auto && {capturedautonomously: this.auto}),
          ...(this.lat != null && {latitude: this.lat}),
          ...(this.lon != null && {longitude: this.lon})
        }
        let jsonObj = JSON.stringify(data);
        console.log("JSON metadata: ", jsonObj);
        //Check if ipfs:// or gateway needs added here
        // check which one is used as contract tokenURI
        files[i].metaHash = await this.IPFSService.addFile(jsonObj);
        console.log("Metadata uploaded, Hash: ", files[i].metaHash);
        let filefound = await this.IPFSService.getFile(files[i].metaHash);
        console.log("file retrieved as: ", uint8ArrayToString(filefound);
      }
    }else {
      for(let i=0; i<files.length; i++){
        let data = {
          name: files[i].mname,
          description: files[i].desc,
          image: "ipfs://" + files[i].imghash,
          ...(files[i].drone != null && {dronetype: files[i].drone}),
          ...(files[i].pilot != null && {pilotname: files[i].pilot}),
          ...(files[i].date != null && {date: files[i].date}),
          ...(files[i].alt != null && {altitude: files[i].alt}),
          ...(files[i].auto && {capturedautonomously: files[i].auto}),
          ...(files[i].lat != null && {latitude: files[i].lat}),
          ...(files[i].lon != null && {longitude: files[i].lon})
        }
        let jsonObj = JSON.stringify(data);
        console.log("JSON metadata: ", jsonObj);
        console.log("Obj type: ", typeof jsonObj);
        files[i].metaHash = await this.IPFSService.addFile(jsonObj);
        console.log("Metadata uploaded, Hash: ", files[i].metaHash);
        let found = await this.IPFSService.getFile(files[i].metaHash);
        console.log("file retrieved as: ", found);
      }
    } // end else
    //investigate wrapping ipfs files in directory

    this.showcontracts = true;
  }

  //display the contracts selections if user pressed submit on ipfs form
  //or did not use metadata
  showContracts() {
    this.showcontracts = true;
  }

}
<!-- UPLOADING FILES -->
<div *ngIf="showform == false" class="all">
<div class="upload">
	<h1> Upload Imagery</h1>
	<input class="form-controlfile" type="file" multiple= "multiple" accept="image/*"
		(change)="onChange($event)">
	<button (click)="onClick()" class="successbtn">
		Upload
	</button>
	<div class="uploadSuccess" *ngIf="msg==='Success'">
			Upload Successful
	</div>
</div>

<div class="preview" *ngIf="FileInfos[0]">
	<h2> Uploaded Files </h2>
	<div class="file-list" *ngFor="let file of FileInfos; index as i">
		<hr>
		<img [src]="file.url" height="50px" width="50px">
		<span class="name"> {{file.name}} </span>
		<button class="removebtn" (click)="onRemove(file)"> X </button>
	</div>
</div>
</div>

<!-- Ask if user wants to use IPFS-->
<div class="ipfs" *ngIf="FileInfos[0] && showform==false">
	<h1 class="ipfsTitle">Upload images with IPFS metadata?:</h1>
	<button (click)="ipfsUpload()" class="ipfsbtn"> Yes </button>
	<button class="noipfsbtns" (click)="showContracts()"> No </button>
</div>

<!-- METADATA FORMS -->
<!-- checkbox to sync with previous = all forms are the same-->
<!-- show extra metadata is possible -> put in platform: type of drone that was flying-->
<!-- pilot name, date (date field not string), altitude (number), autonomous (boolean), lat/long (decimal)-->
<div class="formcontainer" *ngIf="showform==true && showcontracts==false">
	<h1 class="metatitle"> Enter Metadata Information </h1>

	<label for="sameVars"> Use same metadata for all images: </label>
	<input type="checkbox" [(ngModel)]="sameVars" name="sameVars">

	<div *ngIf="!sameVars; else linked"> <!--use non linked form -->
	<form #metaForm="ngForm" (ngSubmit)="submitMeta(FileInfos, sameVars)">
		<div class="innerform" *ngFor="let file of FileInfos; index as i">
		<hr>
			<img [src]="file.url" class="formimg" height="50px" width="50px">
			<p class="filename"> {{ file.name }} </p>

				<div class="form-group">
  				<label for="metaname_{{file.id}}">Name:</label>
  			<input type="text" class="namefield" id="metaname_{{file.id}}"
								[(ngModel)]="file.mname" name="name_{{file.id}}">
				</div>

				<div class="form-group">
  				<label for="metades_{{file.id}}">Description:</label>
  				<input type="text" class="desfield" id="metades_{{file.id}}"
								 [(ngModel)]="file.desc" name="description_{{file.id}}">
				</div>

				<div class="image-group">
					<label for="hash_{{file.id}}"> Image:</label>
					<input type="text" class="imagefield" name="image_{{file.id}}"
								value="{{file.imghash}}" disabled>
				</div>

				<mat-expansion-panel class="panel">
    			<mat-expansion-panel-header class="panelheader">
						<mat-panel-title>Show Additional Fields</mat-panel-title>
    					</mat-expansion-panel-header>

						<div class="drone-group">
			  			<label for="drone_{{file.id}}">Drone Type:</label>
			  			<input type="text" class="dronefield" id="drone_{{file.id}}"
										[(ngModel)]="file.drone" name="drone_{{file.id}}">
						</div>

						<div class="pilot-group">
		  				<label for="pilot_{{file.id}}">Pilot Name:</label>
		  				<input type="text" class="pilotfield" id="pilot_{{file.id}}"
										 [(ngModel)]="file.pilot" name="pilot_{{file.id}}">
						</div>

						<div class="form-group">
		  				<label for="date_{{file.id}}">Date Captured:</label>
		  				<input type="date" class="datefield" id="date_{{file.id}}"
										 [(ngModel)]="file.date" name="date_{{file.id}}">
						</div>

						<div class="form-group">
		  				<label for="alt_{{file.id}}">Altitude:</label>
		  				<input type="number" class="altfield" id="alt_{{file.id}}"
										 [(ngModel)]="file.alt" name="alt_{{file.id}}">
						</div>

						<div class="form-group">
							<label for="auto_{{file.id}}">Captured Autonomously:</label>
							<input type="checkbox" class="autofield" id="auto_{{file.id}}"
										 [(ngModel)]="file.auto" name="auto_{{file.id}}">
						</div>

						<div class="form-group">
							<label for="lat_{{file.id}}">Latitude:</label>
							<input type="number" class="latfield" id="lat_{{file.id}}"
										 [(ngModel)]="file.lat" name="lat_{{file.id}}">
						</div>

						<div class="form-group">
							<label for="lon_{{file.id}}">Longitude:</label>
							<input type="number" class="lonfield" id="lon_{{file.id}}"
										 [(ngModel)]="file.lon" name="lon_{{file.id}}">
						</div>

  				</mat-expansion-panel>

				</div>

				<button type="submit" class="btn-form" [disabled]="!metaForm.form.valid">Submit</button>
        <button type="button" class="btnresetform" (click)="metaForm.reset()">Reset</button>


			</form>
			</div> <!--non-linked form -->

			<ng-template #linked> <!--linked form -->
			<form #metaForm="ngForm" (ngSubmit)="submitMeta(FileInfos, sameVars)">
				<div class="innerform" *ngFor="let file of FileInfos; index as i">
				<hr>
					<img [src]="file.url" height="30px" width="30px">
					<p class="filename"> Image: {{ file.name }} </p>

						<div class="form-group">
		  				<label for="metaname_{{file.id}}">Name:</label>
		  			<input type="text" class="namefield" id="metaname_{{file.id}}"
										[(ngModel)]="metaname" name="name_{{file.id}}">
						</div>

						<div class="form-group">
		  				<label for="metades_{{file.id}}">Description:</label>
		  				<input type="text" class="desfield" id="metades_{{file.id}}"
										 [(ngModel)]="metades" name="description_{{file.id}}">
						</div>

						<div class="image-group">
							<label for="hash_{{file.id}}"> Image:</label>
							<input type="text" class="imagefield" name="image_{{file.id}}"
										value="{{file.imghash}}" disabled>
						</div>

						<mat-expansion-panel class="panel">
		    			<mat-expansion-panel-header class="panelheader">
								<mat-panel-title>Show Additional Fields</mat-panel-title>
		    					</mat-expansion-panel-header>

								<div class="drone-group">
						  		<label for="drone_{{file.id}}">Drone Type:</label>
						  		<input type="text" class="dronefield" id="drone_{{file.id}}"
												[(ngModel)]="drone" name="drone_{{file.id}}">
								</div>

								<div class="pilot-group">
				  				<label for="pilot_{{file.id}}">Pilot Name:</label>
				  				<input type="text" class="pilotfield" id="pilot_{{file.id}}"
												 [(ngModel)]="pilot" name="pilot_{{file.id}}">
								</div>

								<div class="form-group">
				  				<label for="date_{{file.id}}">Date Captured:</label>
				  				<input type="date" class="datefield" id="date_{{file.id}}"
												 [(ngModel)]="date" name="date_{{file.id}}">
								</div>

								<div class="form-group">
				  				<label for="alt_{{file.id}}">Altitude:</label>
				  				<input type="number" class="altfield" id="alt_{{file.id}}"
												 [(ngModel)]="alt" name="alt_{{file.id}}">
								</div>

								<div class="form-group">
									<label for="auto_{{file.id}}">Captured Autonomously:</label>
									<input type="checkbox" class="autofield" id="auto_{{file.id}}"
												 [(ngModel)]="auto" name="auto_{{file.id}}">
								</div>

								<div class="form-group">
									<label for="lat_{{file.id}}">Latitude:</label>
									<input type="number" class="latfield" id="lat_{{file.id}}"
												 [(ngModel)]="lat" name="lat_{{file.id}}">
								</div>

								<div class="form-group">
									<label for="lon_{{file.id}}">Longitude:</label>
									<input type="number" class="lonfield" id="lon_{{file.id}}"
												 [(ngModel)]="lon" name="lon_{{file.id}}">
								</div>

		  				</mat-expansion-panel>

						</div>

						<button type="submit" class="btn-form" [disabled]="!metaForm.form.valid">Submit</button>
		        <button type="button" class="btnresetform" (click)="metaForm.reset()">Reset</button>


					</form>
					</ng-template>


</div>

<!-- CONTRACTS SELECTION -->
<div class="contract" *ngIf="showcontracts">
	<form>
	<!-- if user has entered ipfs data, display only ipfs contracts-->
	<!-- store which one they selected -->
	<label for="contract"> Select Contract: </label>
	<select name="contract" id="contract">
    <option value="BasicNFT">NFT Tokens with no metdata</option>
		<option value="IPFSNFT">NFT Tokens with IPFS metadata</option>
		<option value="nonTransferbasicNFT">Non Transferrable NFT Tokens with no metadata</option>
		<option value="noTransferIPFSNFT">Non Transferrable NFT Tokens with IPFS metadata</option>
	</select>
	</form>
</div>
