import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { from } from 'rxjs';
import { Web3Service } from './web3.service'

const noTbasicNFTArtifacts = require('../../../build/contracts/noTransferbasicNFT.json');
const contract = require('@truffle/contract');

@Injectable({
  providedIn: 'root'
})
export class NotransfercontractService {

  noTransfer = contract(noTbasicNFTArtifacts);

  constructor( private web3Ser: Web3Service ) {
  	// Bootstrap the MetaCoin abstraction for Use
  	this.noTransfer.setProvider(web3Ser.web3.currentProvider);
  }

  getBalance(account: any): Observable<number> {
  let not;

  return Observable.create((observer: any) => {
    this.noTransfer
      .deployed()
      .then((instance: any) => {
        not = instance;
        //we use call here so the call doesn't try and write, making it free
        return not.balanceOf.call(account, {
          from: account
        });
      })
      .then((value: any) => {
        observer.next(value)
        observer.complete()
      })
      .catch((e: any) => {
        console.log(e);
        observer.error(e)
      });
    })
  }

  getOwner(id: any): Observable<number> {
		let not;

		return Observable.create((observer: any) => {
			this.noTransfer
				.deployed()
				.then((instance: any) => {
					not = instance;
					//we use call here so the call doesn't try and write, making it free
					return not.ownerOf.call(id, {
						from: this.web3Ser.web3.eth.defaultAccount
					});
				})
				.then((value: any) => {
					observer.next(value)
					observer.complete()
				})
				.catch((e: any) => {
					console.log(e);
					observer.error(e)
				});
		})
	}

	async mintCoin(to: any): Promise<any>{
		let not = await this.noTransfer.deployed();
    const value = await not.mint(to, {from: this.web3Ser.web3.eth.defaultAccount});
    return value;
		/*return Observable.create((observer: any) => {
			this.noTransfer
				.deployed()
				.then((instance: any) => {
					not = instance;
					return not.mint(to, {from: this.web3Ser.web3.eth.defaultAccount});
				})
				.then(() => {
					observer.next()
					observer.next()
				})
				.catch((e: any) => {
					console.log(e);
					observer.error(e)
				});
		}) */
	}

}
