import { Component, HostListener, NgZone, OnInit, Input } from '@angular/core';
import { Web3Service } from '../services/web3.service';
//contract services
import { BasicContractService } from '../services/basiccontract.service';
import { IpfscontractService } from '../services/ipfscontract.service';
import { NotransfercontractService } from '../services/notransfercontract.service';
import { NotransferipfscontractService } from '../services/notransferipfscontract.service';

@Component({
  selector: 'app-mint',
  templateUrl: './mint.component.html',
  styleUrls: ['./mint.component.css']
})
export class MintComponent implements OnInit {
  @Input() filesList: any;
  @Input() userContract: any;
  window: any
  //metamask info
  account?: any;
  accounts?: any;

  balance?: number;
  tokenID?: number;
  getID?: any;
  tokenURI?: any;
  ownerAddr?: number;
  recipientAddress?: string;
  finished: boolean = false;

  //ngOnInit(): void {}

  constructor(
    private _ngZone: NgZone,
    private web3Service: Web3Service,
    private basicService: BasicContractService,
    private ipfsconService: IpfscontractService,
    private ntbasicService: NotransfercontractService,
    private ntipfsService: NotransferipfscontractService
    ) {
    this.onReady();
  }

  onReady = () => {
    // Get the initial account balance so it can be displayed.
    this.web3Service.getAccounts().subscribe(accs => {
      this.accounts = accs;
      this.account = this.accounts[0];
      console.log("Your contract is: ", this.userContract);
      console.log("Your account is: ", this.account);
    }, err => alert(err))
  };

  ngOnInit(): void {}

  async checkBalance() {
    if (this.userContract == 'basicNFT'){
      const balance = await this.basicService.getBalance(this.account);
      this.balance = balance;
    } else if (this.userContract == 'IPFSNFT'){
        const balance = await this.ipfsconService.getBalance(this.account);
        this.balance = balance;
    } else if (this.userContract == 'noTransferbasicNFT'){
        const balance = await this.ntbasicService.getBalance(this.account);
        this.balance = balance;
    } else {
        const balance = await this.ntipfsService.getBalance(this.account);
        this.balance = balance;
     }
  }

  async getOwner() {
    if(this.userContract == 'basicNFT'){
      const owner = await this.basicService.getOwner(this.getID);
      console.log(owner);
      this.ownerAddr = owner;

   } else if (this.userContract == 'IPFSNFT'){
      const owner = await this.ipfsconService.getOwner(this.getID);
      this.ownerAddr = owner;
      console.log("owner is ", this.ownerAddr);

   } else if (this.userContract == 'noTransferbasicNFT'){
      const owner = await this.ntbasicService.getOwner(this.getID);
      this.ownerAddr = owner;
      console.log("owner is ", this.ownerAddr);

  } else {
      const owner = await this.ntipfsService.getOwner(this.getID)
      this.ownerAddr = owner;
      console.log("owner is ", this.ownerAddr);
    }
  }

 async mintCoin () {
    if(this.userContract == 'basicNFT'){
      for(let i=0; i < this.filesList.length; i++ ){
        const response = await this.basicService.mintCoin(this.account);
        console.log("MINTING SUCCESSFUL");
        console.log("RESPONSE", response);
        this.filesList[i].tokenID = response.logs[0].args.tokenId.toNumber();
        console.log(this.tokenID);
      }
      this.finished = true;
    } else if (this.userContract == 'IPFSNFT'){
      for(let i=0; i < this.filesList.length; i++ ){
        const response = await this.ipfsconService.mintCoin(this.account, this.filesList[i].imghash, this.filesList[i].metaHash);
        console.log("MINTING SUCCESSFUL");
        console.log("RESPONSE", response);
        this.filesList[i].tokenID = response.logs[0].args.tokenId.toNumber();
      }
      this.finished = true;
    } else if (this.userContract == 'noTransferbasicNFT'){
      for(let i=0; i < this.filesList.length; i++ ){
        const response = await this.ntbasicService.mintCoin(this.account);
        console.log("MINTING SUCCESSFUL");
        console.log("RESPONSE", response);
        this.filesList[i].tokenID = response.logs[0].args.tokenId.toNumber();
      }
      this.finished = true;
    } else {
      for(let i=0; i < this.filesList.length; i++ ){
        const response = await this.ntipfsService.mintCoin(this.account, this.filesList[i].imghash, this.filesList[i].metaHash);
        console.log("MINTING SUCCESSFUL");
        console.log("RESPONSE", response);
        this.filesList[i].tokenID = response.logs[0].args.tokenId.toNumber();
      }
      this.finished = true;
    }
  }//end mint coin

  //METADATA ONLY contracts
  async getURI(nftID: number) {
    if(this.userContract == 'IPFSNFT'){
      const tokenURI = await this.ipfsconService.getTokenUri(nftID);
      this.tokenURI = tokenURI;
      console.log("URI is ", this.tokenURI);
    } else if(this.userContract == 'noTransferIPFSNFT') {
      const tokenURI = await this.ntipfsService.getTokenUri(nftID);
      this.tokenURI = tokenURI;
      console.log("URI is ", this.tokenURI);
    } else {
      console.log("Your tokens do not contain Metadata");
    }
  }

}
