  <label class="label">To Address: </label>
    <input [(ngModel)]="recipientAddress" name="recipientAddress"
      class="addressinput"
      type="text"
      placeholder="{{account}}"
      name="recipientAddress"
      required
      #recipientAddressModel="ngModel">
  <button class="mint-btn" (click)="mintCoin()"> Mint </button>
  </div>

  <div class="summary" *ngIf="finished">
    <h1> Minting Summary </h1>
      <p class="result"> {{ filesList.length }} {{ userContract }} NFTs minted
        to account {{ recipientAddress }} </p>
          <div *ngFor="let file of filesList; index as i">
              <p> Image: {{ file.name }} </p>
              <p> TokenID: {{ file.tokenID }} </p>
              <p *ngIf="file.tokenURI"> Token Metadata: {{ file.tokenURI }} </p>
            </div>

      <ul> To view NFTs in wallet:
        <li> 1) Use add token button in metamask wallet </li>
        <li> 2) Enter contract address: 0x2910BbD2EA46b7857Ac04EC15E9d2368Ec0c5421 </li>
        <li> 3) Set token decimal to 0 </li>
        <li> 4) NFTs should now appear under assets in wallet </li>
      </ul>
  </div>

<div class="Ownerbox" *ngIf="finished">
    <h1> Check Owner </h1>
    <label class="label">Token ID</label>
    <p class="control">
      <input
        [(ngModel)]="getID"
        name="ID"
        type="text"
        placeholder="1"
        #tokenIDModel="ngModel"
      >
    </p>
    <button (click)="getOwner()"> Check </button>
    <p *ngIf="ownerAddr"> Owner of NFT {{ tokenID }} is account {{ this.ownerAddr }} </p>

    <h1> Get Token Metadata </h1>
    <label class="label"> Token ID </label>
    <input [(ngModel)]="getID" type="text">
    <button (click)="getURI(getID)"> Check </button>
    <p *ngIf="tokenURI"> Token URI for NFT {{ getID }} is {{ tokenURI }} </p>

  </div>
