        }

        const reader = new FileReader();
        reader.readAsDataURL(this.selectedFiles[i]); //read file content

        reader.onload = event => {
          var image = reader.result;

          if (image) {
            let imageInfo = {
              id: i,
              name: name,
              url: image,
              blob: file,
              imghash: null,
              mname: null,
              desc: null,
              pilot: null,
              date: null,
              alt: null,
              auto: null,
              lat: null,
              lon: null
