                return 1;
              }

              if (name1 < name2) {
                return -1;
              }

              return 0;
            });
          } else {
            //FileReader returned no url
            console.log("FAIL: File could not be read");
            this.msg = "Fail";
          }
        }; //end onload event

      } //end for

    } //end if

  } // end onClick()
  //file remove button


