												 [(ngModel)]="date" name="date_{{file.id}}">
								</div>

								<div class="form-group">
				  				<label for="alt_{{file.id}}">Altitude:</label>
				  				<input type="number" class="altfield" id="alt_{{file.id}}"
												 [(ngModel)]="alt" name="alt_{{file.id}}">
								</div>

								<div class="form-group">
									<label for="auto_{{file.id}}">Captured Autonomously:</label>
									<input type="checkbox" class="autofield" id="auto_{{file.id}}"
												 [(ngModel)]="auto" name="auto_{{file.id}}">
								</div>

								<div class="form-group">
									<label for="lat_{{file.id}}">Latitude:</label>
									<input type="number" class="latfield" id="lat_{{file.id}}"
												 [(ngModel)]="lat" name="lat_{{file.id}}">
								</div>

								<div class="form-group">
									<label for="lon_{{file.id}}">Longitude:</label>
