												 [(ngModel)]="lon" name="lon_{{file.id}}">
								</div>

		  				</mat-expansion-panel>

						</div>

						<button type="submit" class="btn-form" [disabled]="!metaForm.form.valid">Submit</button>
		        <button type="button" class="btnresetform" (click)="metaForm.reset()">Reset</button>


					</form>
					</ng-template>


</div>
