import { Component, HostListener, NgZone, OnInit, Input } from '@angular/core';
import { Web3Service } from '../services/web3.service';
//contract services
import { BasicContractService } from '../services/basiccontract.service';
import { IpfscontractService } from '../services/ipfscontract.service';
import { NotransfercontractService } from '../services/notransfercontract.service';
import { NotransferipfscontractService } from '../services/notransferipfscontract.service';

@Component({
  selector: 'app-mint',
  templateUrl: './mint.component.html',
  styleUrls: ['./mint.component.css']
})
export class MintComponent implements OnInit {
  @Input() filesList: any;
  @Input() userContract: any;
  window: any
  //metamask info
  account?: any;
  accounts?: any;

  metacontract?: boolean
  balance?: number;
  tokenID?: number;
  tokenURI?: any;
  recipientAddress?: string;
  finished: boolean = false;

  //form vars
  getID?: number;
  getownerID?: number;
  ownerAddr?: string;

  //ngOnInit(): void {}

  constructor(
    private _ngZone: NgZone,
    private web3Service: Web3Service,
    private basicService: BasicContractService,
    private ipfsconService: IpfscontractService,
    private ntbasicService: NotransfercontractService,
    private ntipfsService: NotransferipfscontractService
    ) {
    this.onReady();
  }

  async onReady() {
    this.accounts = await this.web3Service.getAccounts();
    this.account = this.accounts[0];
    console.log("Your contract is: ", this.userContract);
    console.log("Your account is: ", this.account);
  /*  this.web3Service.getAccounts().subscribe(accs => {
      this.accounts = accs;
      this.account = this.accounts[0];
      console.log("Your contract is: ", this.userContract);
      console.log("Your account is: ", this.account);
      this.metacontract = this.contractType();
      console.log(this.metacontract);
    }, err => alert(err)) */
  }

  ngOnInit(): void {}

  contractType(): boolean {
    if (this.userContract == 'basicNFT' || this.userContract == 'noTransferbasicNFT'){
      return false;
    } else {
      return true;
    }
  }

  async checkBalance() {
    if (this.userContract == 'basicNFT'){
      const balance = await this.basicService.getBalance(this.account);
      this.balance = balance;
    } else if (this.userContract == 'IPFSNFT'){
        const balance = await this.ipfsconService.getBalance(this.account);
        this.balance = balance;
    } else if (this.userContract == 'noTransferbasicNFT'){
        const balance = await this.ntbasicService.getBalance(this.account);
        this.balance = balance;
    } else {
        const balance = await this.ntipfsService.getBalance(this.account);
        this.balance = balance;
     }
  }

  async getOwner() {
    if(this.userContract == 'basicNFT'){
      const owner = await this.basicService.getOwner(this.getownerID);
      console.log(owner);
      this.ownerAddr = owner;

   } else if (this.userContract == 'IPFSNFT'){
      const owner = await this.ipfsconService.getOwner(this.getownerID);
      this.ownerAddr = owner;
      console.log("owner is ", this.ownerAddr);

   } else if (this.userContract == 'noTransferbasicNFT'){
      const owner = await this.ntbasicService.getOwner(this.getownerID);
      this.ownerAddr = owner;
      console.log("owner is ", this.ownerAddr);

  } else {
      const owner = await this.ntipfsService.getOwner(this.getownerID)
      this.ownerAddr = owner;
      console.log("owner is ", this.ownerAddr);
    }
  }

 async mintCoin () {
    if(this.userContract == 'basicNFT'){
      for(let i=0; i < this.filesList.length; i++ ){
        const response = await this.basicService.mintCoin(this.account);
        console.log("MINTING SUCCESSFUL");
        console.log("RESPONSE", response);
        this.filesList[i].tokenID = response.logs[0].args.tokenId.toNumber();
        console.log(this.filesList[i].tokenID);
      }
      this.finished = true;
    } else if (this.userContract == 'IPFSNFT'){
      for(let i=0; i < this.filesList.length; i++ ){
        const response = await this.ipfsconService.mintCoin(this.account, this.filesList[i].imghash, this.filesList[i].metaHash);
        console.log("MINTING SUCCESSFUL");
        console.log("RESPONSE", response);
        this.filesList[i].tokenID = response.logs[0].args.tokenId.toNumber();
      }
      this.finished = true;
    } else if (this.userContract == 'noTransferbasicNFT'){
      for(let i=0; i < this.filesList.length; i++ ){
        const response = await this.ntbasicService.mintCoin(this.account);
        console.log("MINTING SUCCESSFUL");
        console.log("RESPONSE", response);
        this.filesList[i].tokenID = response.logs[0].args.tokenId.toNumber();
      }
      this.finished = true;
    } else {
      for(let i=0; i < this.filesList.length; i++ ){
        const response = await this.ntipfsService.mintCoin(this.account, this.filesList[i].imghash, this.filesList[i].metaHash);
        console.log("MINTING SUCCESSFUL");
        console.log("RESPONSE", response);
        this.filesList[i].tokenID = response.logs[0].args.tokenId.toNumber();
      }
      this.finished = true;
    }
  }//end mint coin

  //METADATA ONLY contracts
  async getURI() {
    if(this.userContract == 'IPFSNFT'){
      const tokenURI = await this.ipfsconService.getTokenUri(this.getID);
      this.tokenURI = tokenURI;
      console.log("URI is ", this.tokenURI);
    } else if(this.userContract == 'noTransferIPFSNFT') {
      const tokenURI = await this.ntipfsService.getTokenUri(this.getID);
      this.tokenURI = tokenURI;
      console.log("URI is ", this.tokenURI);
    } else {
      console.log("Your tokens do not contain Metadata");
    }
  }

}
<div class="minter" *ngIf="finished==false">
  <h1> Mint NFTs </h1>
  <label class="label">To Address: </label>
    <input class="addressinput"
      type="text"
      [value]="account">

  <button class="mint-btn" (click)="mintCoin()"> Mint </button>
  </div>

  <div class="summary" *ngIf="finished">
    <h1> Minting Results </h1>
      <p class="result"> {{ filesList.length }} {{ userContract }} NFTs minted
        to account {{ recipientAddress }} </p>
          <div *ngFor="let file of filesList; index as i">
              <hr>
              <p> Image: {{ file.name }} </p>
              <p> TokenID: {{ file.tokenID }} </p>
              <p *ngIf="file.tokenURI"> Token Metadata: {{ file.tokenURI }} </p>
            </div>
            <hr>
            <h1> To view NFTs in wallet: </h1>
            <div class="result">
              <ol>
                <li> Use add token button in metamask wallet </li>
                <li> Enter contract address:  </li>
                <li> Set token decimal to 0 </li>
                <li> NFTs should now appear under assets in wallet </li>
            </ol>
          </div>
  </div>

<div class="Ownerbox" *ngIf="finished">
    <h1> Check Owner </h1>
    <label class="label">Token ID</label>
    <input [(ngModel)]="getownerID"
      name="ID"
      type="text"
      placeholder="1"
      #ownerIDModel="ngModel"
      >
    <button (click)="getOwner()"> Check </button>
    <p *ngIf="ownerAddr"> Owner of NFT {{ getownerID }} is account {{ this.ownerAddr }} </p>

    <div *ngIf="metacontract==true">
      <h1> Get Token Metadata </h1>
      <label class="label"> Token ID </label>
      <input [(ngModel)]="getID" value="1" type="text" #tokenIDmodel="ngModel">
      <button (click)="getURI()"> Check </button>
      <p *ngIf="tokenURI"> Token URI for NFT {{ getID }} is {{ tokenURI }} </p>
    </div>
  </div>
