import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { IpfsService } from "../ipfs.service";

@Component({
  selector: 'app-upload',
  templateUrl: './upload.component.html',
  styleUrls: ['./upload.component.css']
})
export class UploadComponent implements OnInit {
  selectedFiles?: FileList;         //files selected by user with file broswer
  msg?: string;                    // holds message associated with upload
  FileInfos: any[] = [];          // list of all files uploaded
  reUploads: string[] = [];      // files in selected files that are already uploaded
  hash?: string;
  id: string | null = null;
  metadata: boolean = false;

  constructor(private IPFSService: IpfsService) { }

  ngOnInit(): void {
    this.start();
  }

  async start() {
    const id = await this.IPFSService.getId();
    this.id = id.id;
    if(this.id == null){
      console.log("IPFS start up FAILED");
    }
  }

  //on click of choose files button
  onChange(event: any) {
    this.selectedFiles = event.target.files;
  }
  //on click of upload button
  onClick() {
    this.msg = "";
    this.reUploads = [];
    if(this.selectedFiles && this.selectedFiles[0]) { //if user has selected a file
      const numOfFiles = this.selectedFiles.length;  //gets length of selected files list

      for (let i = 0; i < numOfFiles; i++) { //for files selected
        let name = this.selectedFiles[i].name;
        let file = this.selectedFiles[i];
        let match = false;
        for (var t in this.FileInfos) {  //check if file has already been uploaded
            if (this.FileInfos[t].name == name) {
              //console.log("new file's name: " + name + "MATCHED with file IN checking LOOP" + this.FileInfos[t].name);
              match = true;
            }
          }

      if(match){
        this.reUploads.push(name);
        //this.msg = "matched";
        continue;  //skip to next iteration
      }

      const reader = new FileReader();
      reader.readAsDataURL(this.selectedFiles[i]); //read file content
      reader.onload = (event) => {                //when image is loaded
        var image = reader.result as string;
        if(image){
          let imageInfo = {
            name: name,
            url: image,
            blob: file,
            imghash: null
          };
          this.FileInfos.push(imageInfo);
          this.msg = "Success";

          //sort alphabetically by name
          const sorted = this.FileInfos.sort((t1, t2) => {
          const name1 = t1.name.toLowerCase();
          const name2 = t2.name.toLowerCase();
          if (name1 > name2) { return 1; }
          if (name1 < name2) { return -1; }
          return 0;
        })
      } else { //FileReader returned no url
          console.log("FAIL: File could not be read");
          this.msg = "Fail";
          }
        }; //end onload event
      } //end for
    } //end if
  } // end onClick()

  onRemove(imageObj: any) {
    this.msg = "";
    var index = this.FileInfos.indexOf(imageObj);
    this.FileInfos.splice(index,1);
  }

  async ipfsUpload() {
    this.metadata = true;
    if(this.FileInfos && this.FileInfos[0]) { //if user has selected a file
      const len = this.FileInfos.length;
      for(let i = 0; i < len; i++){
        this.FileInfos[i].imghash = await this.IPFSService.addFile(this.FileInfos[i].blob);
        console.log("FILE ADDED. Name: " + this.FileInfos[i].name + "Hash: " + this.FileInfos[i].imghash);
      }
    }
  }

}
<div *ngIf="metadata == false" class="all">
<div class="upload">
	<h1> Upload Imagery</h1>
	<input class="form-control" type="file" multiple= "multiple" accept="image/*"
		(change)="onChange($event)">
	<button (click)="onClick()" class="btn-btn-success">
		Upload
	</button>
	<div class="successBox" *ngIf="msg==='Success'">
			Upload Successful
	</div>
</div>

<div class="preview" *ngIf="FileInfos[0]">
	<h2> Uploaded Files </h2>
	<div class="file-list" *ngFor="let file of FileInfos; index as i">
		<hr>
		<img [src]="file.url" height="50px" width="50px">
		<span class="name"> {{file.name}} </span>
		<button class="remove" (click)="onRemove(file)"> X </button>
	</div>
</div>
</div>

<!-- <div class="dropdown" *ngIf="FileInfos[0]">
	<h1 class="menuTitle">Enter Minting Information:</h1>
	<form>
	<label for="contract"> Select Contract: </label>
	<select name="contract" id="contract">
    <option value="BasicNFT">NFT Tokens with no metdata</option>
		<option value="IPFSNFT">NFT Tokens with IPFS metadata</option>
		<option value="nonTransferbasicNFT">Non Transferrable NFT Tokens with no metadata</option>
		<option value="noTransferIPFSNFT">Non Transferrable NFT Tokens with IPFS metadata</option>
	</select>
	</form>
</div>  -->

<div class="ipfs" *ngIf="FileInfos[0]">
	<h1 class="menuTitle">Upload images with IPFS metadata?:</h1>
	<button (click)="ipfsUpload()" class="ipfsUpload"> Yes </button>
	<button class="noUpload"> No </button>
</div>

<div class="metadform" *ngIf="metadata == true">
	<h1 class="metatitle"> Metadata Information </h1>
	<div *ngFor="let file of FileInfos">
			<p> Image: {{ file.name }} </p>
			<p> IPFS hash: {{file.imghash}} </p>
		</div>
</div>
